package IrisRecog;/** * Constants.java * * Abstract class defines the static constants used throughout the * application. * */import java.awt.*;public abstract class Constants{	/*	 * The default background color	 */	public static final Color CLR_DEFAULT_BACKGROUND = SystemColor.control;	/*	 * The JPG file extension	 */	public static final String STR_JPEG_EXTENSION = "jpeg";	/*	 * The JPEG file extension	 */    public static final String STR_JPG_EXTENSION = "jpg";	/*	 * The Agent file extension	 */    public static final String STR_AGENT_EXTENSION = "agt";	/*	 * The width of the unwrapped (unrolled) iris image	 */	public static final int INT_UNWRAPPED_WIDTH = 512;	/*	 * The height of the unwrapped (unrolled) iris image	 */	public static final int INT_UNWRAPPED_HEIGHT = 64;	/*	 * The width of the main application window	 */	public static final int INT_APPLICATION_WINDOW_WIDTH = 700;	/*	 * The height of the main application window	 */	public static final int INT_APPLICATION_WINDOW_HEIGHT = 725;	/*	 * Value to use as a small gap space	 */	public static final int INT_SMALL_GAP = 5;	/*	 * Value to use as a medium gap space	 */	public static final int INT_MEDIUM_GAP = 10;	/*	 * Value to use as a large gap space	 */	public static final int INT_LARGE_GAP = 20;	public static final int DEFAULT_THRESHOLD = 128;}